import React, { useEffect, useState } from 'react';
import styles from './styles.module.css';

const ProductList = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch('http://localhost:8080/api/products');
        if (!response.ok) throw new Error('Failed to fetch products');
        const data = await response.json();
        setProducts(data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  return (
    <div>
      <h1>Product List</h1>
      <div className={styles.productList}>
        {products.map((product) => (
          <div key={product._id} className={styles.productCard}>
            <img 
              src={`http://localhost:8080/uploads/${product.image}`} 
              alt={product.title} 
              className={styles.productImage}
            />
            <div className={styles.productInfo}>
              <h3>{product.title}</h3>
              <p className={styles.description}>Size: {product.size}</p>
              <p className={styles.description}>Condition: {product.condition}</p>
              <div className={styles.preferences}>
                {product.preferences.map((preference, index) => (
                  <span key={index} className={styles.preference}>{preference}</span>
                ))}
              </div>
              <p className={styles.postedBy}>
                Posted by: {product.postedBy?.firstName} {product.postedBy?.lastName}
              </p>
              <p className={styles.postedOn}>
                Posted on: {new Date(product.postedDate).toLocaleDateString()}
              </p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
