// ProductDetails.jsx
import React, { useEffect, useState } from 'react';

const ProductDetails = ({ productId }) => {
  const [product, setProduct] = useState(null); // State to store product details
  const [error, setError] = useState(null); // State to handle errors

  // Fetch product details by ID
  useEffect(() => {
    const fetchProductDetails = async () => {
  try {
    const response = await fetch(`http://localhost:8080/api/products/${productId}`);
    if (!response.ok) {
      throw new Error(`Failed to fetch product details with status: ${response.status}`);
    }
    const data = await response.json();
    setProduct(data);
  } catch (error) {
    console.error('Error fetching product details:', error);
    setError(error.message);
  }
};


    fetchProductDetails();
  }, [productId]);

  // Render loading state
  if (!product && !error) {
    return <p>Loading...</p>;
  }

  // Render error state
  if (error) {
    return <p>Error: {error}</p>;
  }

  // Render product details
  return (
    <div>
      <h1>{product.title}</h1>
      <p><strong>Size:</strong> {product.size}</p>
      <p><strong>Condition:</strong> {product.condition}</p>
      <p><strong>Preferences:</strong> {product.preferences && product.preferences.length > 0 
        ? product.preferences.join(', ') 
        : 'None'}</p>
      {product.image && (
        <img src={product.image} alt={product.title} style={{ maxWidth: '300px', maxHeight: '300px' }} />
      )}
    </div>
  );
};

export default ProductDetails;
