import React, { useEffect, useState } from 'react';

const ProductDetails = ({ productId }) => {
  const [product, setProduct] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProductDetails = async () => {
      try {
        const response = await fetch(`http://localhost:8080/api/products/${productId}`);
        if (!response.ok) {
          throw new Error(`Failed to fetch product details with status: ${response.status}`);
        }
        const data = await response.json();
        setProduct(data);
      } catch (error) {
        setError(error.message);
      }
    };

    fetchProductDetails();
  }, [productId]);

  if (error) {
    return <p>Error: {error}</p>;
  }

  if (!product) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h1>{product.title}</h1>
      <p><strong>Size:</strong> {product.size}</p>
      <p><strong>Condition:</strong> {product.condition}</p>
      <p><strong>Preferences:</strong> {product.preferences && product.preferences.length > 0 
        ? product.preferences.join(', ') 
        : 'None'}</p>
      {product.image && (
        <img src={product.image} alt={product.title} style={{ maxWidth: '300px', maxHeight: '300px' }} />
      )}
    </div>
  );
};

export default ProductDetails;
