import React, { useEffect, useState } from 'react';

const ProductList = ({ refresh }) => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch('http://localhost:8080/api/products');
        const data = await response.json();  // JSON response
        setProducts(data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };


    fetchProducts();
  }, [refresh]);

  return (
    <div>
      <ul>
        {products.map((product) => (
          <li key={product._id}>
            <h2>{product.title}</h2>
            <p>Size: {product.size}</p>
            <p>Condition: {product.condition}</p>
            <p>Preferences: {product.preferences?.join(', ')}</p>
            <p><strong>Posted by:</strong> {product.username}</p>
            <img
              src={`http://localhost:8080/uploads/${product.image}`}
              alt={product.title}
              width="200"
            />
            <p>Posted on: {new Date(product.postedDate).toLocaleDateString()}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ProductList;
