import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './styles.module.css';

const ProductList = ({ refresh }) => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true); // For loading state
  const [error, setError] = useState(null); // For error state
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch('http://localhost:8080/api/products');
        if (!response.ok) {
          throw new Error(`Failed to fetch products: ${response.statusText}`);
        }
        const data = await response.json();
        console.log('Fetched Products:', data); // Debugging log
        setProducts(data);
      } catch (error) {
        console.error('Error fetching products:', error);
        setError(error.message); // Save error message
      } finally {
        setLoading(false); // Stop loading
      }
    };
    fetchProducts();
  }, [refresh]);

  const handleProductClick = (productId) => {
    navigate(`/items/${productId}`);
  };

  if (loading) {
    return <div className={styles.message}>Loading products...</div>;
  }

  if (error) {
    return <div className={styles.message}>Error: {error}</div>;
  }

  if (products.length === 0) {
    return <div className={styles.message}>No products available.</div>;
  }

  return (
    <div className={styles.productList}>
      {products.map((product) => (
        <div 
          key={product._id} 
          className={styles.productCard}
          onClick={() => handleProductClick(product._id)}
        >
          {product.image && (
            <img 
              src={product.image} 
              alt={product.title || 'Product Image'} 
              className={styles.productImage} 
            />
          )}
          <div className={styles.productInfo}>
            <h3>{product.title || 'No Title Available'}</h3>

            <div className={styles.sizeCondition}>
              <p className={styles.description}>
                Size: {product.size || 'N/A'}
              </p>
              <p className={styles.description}>
                Condition: {product.condition || 'N/A'}
              </p>
            </div>

            <div className={styles.preferencesContainer}>
              <p className={styles.description}>Preferences:</p>
              <div className={styles.preferences}>
                {product.preferences && product.preferences.length > 0 ? (
                  product.preferences.map((pref, index) => (
                    <span key={index} className={styles.preference}>
                      {pref}
                    </span>
                  ))
                ) : (
                  <span className={styles.noPreference}>No preferences provided</span>
                )}
              </div>
            </div>

            <p className={styles.postedBy}>
              Posted on: {product.postedDate 
                ? new Date(product.postedDate).toLocaleDateString() 
                : 'Date not available'}
            </p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default ProductList;
