import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './styles.module.css';

const NewItem = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    title: '',
    size: '',
    condition: '',
  });
  const [images, setImages] = useState([]);
  const [preferences, setPreferences] = useState([]);
  const [newPreference, setNewPreference] = useState('');
  const [previewImages, setPreviewImages] = useState([]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    if (files.length > 3) {
      alert('You can only upload up to 3 images');
      return;
    }
    setImages(files);
    
    // Create preview URLs
    const previews = files.map(file => URL.createObjectURL(file));
    setPreviewImages(previews);
  };

  const handleAddPreference = () => {
    if (newPreference.trim() && !preferences.includes(newPreference)) {
      setPreferences(prev => [...prev, newPreference]);
      setNewPreference('');
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you would typically send the data to your backend
    console.log({ ...formData, images, preferences });
    navigate('/items');
  };

  return (
    <div className={styles.container}>
      <div className={styles.header}>
        <h2>New Item</h2>
        <button onClick={() => navigate('/items')} className={styles.backButton}>
          Go Back
        </button>
      </div>
      <div className={styles.content}>
        <div className={styles.imagePreview}>
          {previewImages.length > 0 ? (
            previewImages.map((preview, index) => (
              <img 
                key={index} 
                src={preview} 
                alt={`Preview ${index + 1}`} 
                className={styles.previewImage}
              />
            ))
          ) : (
            <div className={styles.placeholderImage}>
              No images selected
            </div>
          )}
        </div>
        <div className={styles.formSection}>
          <h3>Item Details</h3>
          <form onSubmit={handleSubmit}>
            <div className={styles.formGroup}>
              <label>Title</label>
              <input
                type="text"
                name="title"
                value={formData.title}
                onChange={handleInputChange}
                required
              />
            </div>

            <div className={styles.formGroup}>
              <label>Size</label>
              <input
                type="text"
                name="size"
                value={formData.size}
                onChange={handleInputChange}
                required
              />
            </div>

            <div className={styles.formGroup}>
              <label>Condition</label>
              <input
                type="text"
                name="condition"
                value={formData.condition}
                onChange={handleInputChange}
                required
              />
            </div>

            <div className={styles.formGroup}>
              <label>Images (Max 3)</label>
              <input
                type="file"
                accept="image/*"
                multiple
                onChange={handleImageChange}
                className={styles.fileInput}
              />
            </div>

            <div className={styles.formGroup}>
              <label>Preferences</label>
              <div className={styles.preferenceInput}>
                <input
                  type="text"
                  value={newPreference}
                  onChange={(e) => setNewPreference(e.target.value)}
                  placeholder="Add preference"
                />
                <button
                  type="button"
                  onClick={handleAddPreference}
                  className={styles.addPreferenceButton}
                >
                  Add
                </button>
              </div>
              <div className={styles.preferences}>
                {preferences.map((pref, index) => (
                  <span key={index} className={styles.preference}>
                    {pref}
                  </span>
                ))}
              </div>
            </div>

            <button type="submit" className={styles.postButton}>
              Post
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default NewItem;
