import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './styles.module.css';

const NewItem = ({ onNewItemAdded }) => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    title: '',
    size: '',
    condition: '',
  });
  const [image, setImage] = useState(null);
  const [previewImage, setPreviewImage] = useState(null);
  const [preferences, setPreferences] = useState([]);
  const [newPreference, setNewPreference] = useState('');
  const [error, setError] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);

    const reader = new FileReader();
    reader.onloadend = () => {
      setPreviewImage(reader.result);
    };
    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const handleAddPreference = () => {
    if (newPreference.trim() !== '') {
      setPreferences((prevPreferences) => [...prevPreferences, newPreference]);
      setNewPreference('');
    }
  };

  const handleDeletePreference = (preference) => {
    setPreferences((prevPreferences) =>
      prevPreferences.filter((pref) => pref !== preference)
    );
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formDataToSend = new FormData();
    formDataToSend.append('title', formData.title);
    formDataToSend.append('size', formData.size);
    formDataToSend.append('condition', formData.condition);
    formDataToSend.append('preferences', JSON.stringify(preferences));
    formDataToSend.append('image', image);

    try {
      const response = await fetch('http://localhost:8080/api/products', {
        method: 'POST',
        body: formDataToSend,
      });
      if (response.ok) {
        alert('Product added successfully!');
        onNewItemAdded();
        navigate('/items');
      } else {
        alert('Failed to add product.');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('An error occurred.');
    }
  };

  return (
    <div className={styles.container}>
      <div className={styles.header}>
        <h2>New Item</h2>
        <button onClick={() => navigate('/items')} className={styles.backButton}>
          Go Back
        </button>
      </div>
      <div className={styles.content}>
        <div className={styles.imagePreview}>
          {previewImage ? (
            <img src={previewImage} alt="Preview" className={styles.previewImage} />
          ) : (
            <div className={styles.placeholderImage}>No image selected</div>
          )}
        </div>
        <div className={styles.formSection}>
          <h3>Item Details</h3>
          <form onSubmit={handleSubmit}>
            {error && <p className={styles.error}>{error}</p>}
            <div className={styles.formGroup}>
              <label>Title</label>
              <input
                type="text"
                name="title"
                value={formData.title}
                onChange={handleInputChange}
                required
              />
            </div>

            <div className={styles.formGroup}>
              <label>Size</label>
              <input
                type="text"
                name="size"
                value={formData.size}
                onChange={handleInputChange}
                required
              />
            </div>

            <div className={styles.formGroup}>
              <label>Condition</label>
              <input
                type="text"
                name="condition"
                value={formData.condition}
                onChange={handleInputChange}
                required
              />
            </div>

            <div className={styles.formGroup}>
              <label>Image</label>
              <input
                type="file"
                accept="image/*"
                onChange={handleImageChange}
                className={styles.fileInput}
              />
            </div>

            <div className={styles.formGroup}>
              <label>Preferences</label>
              <div className={styles.preferenceInput}>
                <input
                  type="text"
                  value={newPreference}
                  onChange={(e) => setNewPreference(e.target.value)}
                  placeholder="Add preference"
                />
                <button
                  type="button"
                  onClick={handleAddPreference}
                  className={styles.addPreferenceButton}
                >
                  Add
                </button>
              </div>
              <div className={styles.preferences}>
                {preferences.map((pref, index) => (
                  <div key={index} className={styles.preferenceContainer}>
                    <span className={styles.preference}>{pref}</span>
                    <button
                      type="button"
                      onClick={() => handleDeletePreference(pref)}
                      className={styles.deletePreferenceButton}
                    >
                      Delete
                    </button>
                  </div>
                ))}
              </div>
            </div>

            <button type="submit" className={styles.postButton}>
              Post
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default NewItem;
