import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import styles from './styles.module.css';

const NewItem = () => {
  const [title, setTitle] = useState('');
  const [size, setSize] = useState('');
  const [condition, setCondition] = useState('');
  const [preferences, setPreferences] = useState([]);
  const [image, setImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [userDetails, setUserDetails] = useState(null);
  const [newPreference, setNewPreference] = useState('');
  const navigate = useNavigate();

  // Fetch user details (firstName and lastName) from the backend
  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        const res = await axios.get('/api/auth/user'); // Assuming a route to get current logged-in user details
        setUserDetails(res.data);
      } catch (error) {
        console.error('Error fetching user details:', error);
      }
    };
    fetchUserDetails();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('title', title);
    formData.append('size', size);
    formData.append('condition', condition);
    formData.append('preferences', JSON.stringify(preferences));
    formData.append('image', image);

    // Add firstName and lastName to the product details
    if (userDetails) {
      formData.append('firstName', userDetails.firstName);
      formData.append('lastName', userDetails.lastName);
    }

    try {
      const res = await axios.post('/api/products', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      alert(res.data.message);
      navigate('/products');
    } catch (error) {
      console.error('Error adding product:', error);
      alert('Error adding product');
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    setImagePreview(URL.createObjectURL(file));
  };

  const handleAddPreference = () => {
    if (newPreference.trim()) {
      setPreferences([...preferences, newPreference.trim()]);
      setNewPreference('');
    }
  };

  const handleDeletePreference = (pref) => {
    setPreferences(preferences.filter((p) => p !== pref));
  };

  return (
    <div className={styles.container}>
      <div className={styles.header}>
        <h2>Add New Product</h2>
        <button onClick={() => navigate('/products')} className={styles.backButton}>
          Go Back
        </button>
      </div>
      <div className={styles.content}>
        <div className={styles.imagePreview}>
          {image ? (
            <img
              src={imagePreview}
              alt="Image Preview"
              className={styles.previewImage}
            />
          ) : (
            <div className={styles.placeholderImage}>No image selected</div>
          )}
        </div>
        <div className={styles.formSection}>
          <h3>Item Details</h3>
          <form onSubmit={handleSubmit}>
            <div className={styles.formGroup}>
              <label>Title</label>
              <input
                type="text"
                name="title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                required
                className={styles.formInput}
              />
            </div>

            <div className={styles.formGroup}>
              <label>Size</label>
              <input
                type="text"
                name="size"
                value={size}
                onChange={(e) => setSize(e.target.value)}
                required
                className={styles.formInput}
              />
            </div>

            <div className={styles.formGroup}>
              <label>Condition</label>
              <input
                type="text"
                name="condition"
                value={condition}
                onChange={(e) => setCondition(e.target.value)}
                required
                className={styles.formInput}
              />
            </div>

            <div className={styles.formGroup}>
              <label>Preferences</label>
              <div className={styles.preferenceInput}>
                <input
                  type="text"
                  value={newPreference}
                  onChange={(e) => setNewPreference(e.target.value)}
                  placeholder="Add preference"
                  className={styles.formInput}
                />
                <button
                  type="button"
                  onClick={handleAddPreference}
                  className={styles.addPreferenceButton}
                >
                  Add
                </button>
              </div>
              <div className={styles.preferences}>
                {preferences.map((pref, index) => (
                  <div key={index} className={styles.preferenceContainer}>
                    <span className={styles.preference}>{pref}</span>
                    <button
                      type="button"
                      onClick={() => handleDeletePreference(pref)}
                      className={styles.deletePreferenceButton}
                    >
                      Delete
                    </button>
                  </div>
                ))}
              </div>
            </div>

            <div className={styles.formGroup}>
              <label>Image</label>
              <input
                type="file"
                onChange={handleImageChange}
                required
                className={styles.fileInput}
              />
              {imagePreview && <img src={imagePreview} alt="Image Preview" className={styles.previewImage} />}
            </div>

            <button type="submit" className={styles.postButton}>
              Submit
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default NewItem;
