import React from 'react';
import { Link } from 'react-router-dom';
import styles from './styles.module.css';

const ProductList = ({ items }) => {
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      day: 'numeric',
      month: 'short',
      year: 'numeric',
    });
  };

  return (
    <div className={styles.productGrid}>
      {items.map((item) => (
        <Link
          key={item.id}
          to={`/product/${item.id}`}
          className={styles.productCard} // Ensure the Link doesn't affect the existing styles
        >
          <div>
            <div className={styles.imageContainer}>
              <img src={item.image} alt={item.title} className={styles.productImage} />
            </div>
            <div className={styles.productContent}>
              <h3 className={styles.title}>{item.title}</h3>
              <p className={styles.description}>{item.description}</p>
              <p className={styles.postedBy}>
                Posted by {item.postedBy} on {formatDate(item.date)}
              </p>
              <div className={styles.preferencesContainer}>
                {item.preferences.map((pref, idx) => (
                  <span key={idx} className={styles.preference}>
                    {pref}
                  </span>
                ))}
              </div>
              <div className={styles.trades}>Trades: {item.trades}</div>
            </div>
          </div>
        </Link>
      ))}
    </div>
  );
};

export default ProductList;
